    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        String contact = jTextField2.getText();
        int a = JOptionPane.showConfirmDialog(null, "Do you really want to remove this employee?", "select", JOptionPane.YES_NO_OPTION);
        if (a == 0) {
            try {
                Statement st = connectSQL.getConnection().createStatement();
                st.executeUpdate("delete from employee where contactNo='" + contact + "'");
                JOptionPane.showMessageDialog(null, "Successfully Deleted!");
                setVisible(false);
                new deleteEmployee().setVisible(true);
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

